		else
			update bll_T_REQID set CCRID=CCRID+1 where IDType = vIDType;
			select CCRID,MaxID,CRFIX,WS,ILX into vID,vMaxID,vPreFix,vWS,vILX from bll_T_REQID WHERE IDType = vIDType;
      set vCurDate := replace(replace(replace(date_format(now(),'%Y-%c-%d %h:%i:%s'), '-',''),':',''),' ','');
			set vcID:=CONCAT(vID,'');
			/*1数字*/
			if vILX = 1 then
				set CurID:=vcID;
			/*2前缀+数字*/
			elseif vILX = 2 then
				set vcID:= LPAD(vcID,vWS,'0');
				set CurID:=concat(vPreFix,vcID);
			/*日期+数字（订单）*/
			elseif vILX = 3 then
				set vcID:= LPAD(vcID,vWS,'0');
				set CurID:=concat(vCurDate,vcID);
			end if;
	  end if;
    SELECT CurID;	
end
