INSERT INTO `dp_admin_config` VALUES (2, 'web_site_title', '站点标题', 'base', 'text', '泉州微信开发 福建长城信息，提供微信开发、会员管理系统、软件开发、网站设计等全方位企业服务。专业技术开发服务团队,近20年丰富项目经验。', '', '调用方式：<code>config(\'web_site_title\')</code>', '', '', '', '', '', 2, '', '', '', '', 1475240646, 1477710341, 2, 1);
INSERT INTO `dp_admin_config` VALUES (3, 'web_site_slogan', '站点标语', 'base', 'text', '泉州微信开发 福建长城信息，提供微信开发、会员管理系统、软件开发、网站设计等全方位企业服务。专业技术开发服务团队,近20年丰富项目经验。', '', '站点口号，调用方式：<code>config(\'web_site_slogan\')</code>', '', '', '', '', '', 2, '', '', '', '', 1475240994, 1477710357, 3, 1);
