INSERT INTO `dp_admin_config` VALUES (5, 'web_site_description', '站点描述', 'base', 'textarea', '福建省长城信息发展有限公司（总部 泉州）：提供微信开发、微信支付、微信商城、会员管理系统、软件开发、网站设计、O2O、企业OA、云系统、现有系统融合等等技术服务，可以帮助中小企业快速无缝地进行微信公众号应用等线上业务和服务的拓展。公司注册于1998年，注册资金一千万元人民币，拥有专业成熟的技术、设计、开发和运维服务团队，拥有近20年各种项目的丰富经验。', '', '网站描述，有利于搜索引擎抓取相关信息', '', '', '', '', '', 2, '', '', '', '', 1475241186, 1475241186, 6, 1);
INSERT INTO `dp_admin_config` VALUES (6, 'web_site_keywords', '站点关键词', 'base', 'text', '微信开发、小程序开发、公众号开发、微信技术服务、微信代开发、定制开发、泉州微信开发、福建微信开发、泉州技术服务、福建技术服务、软件开发、网站建设、网站设计、H5开发、微信商城、微信支付、会员系统、维护、升级、改版', '', '网站搜索引擎关键字', '', '', '', '', '', 2, '', '', '', '', 1475241328, 1475241328, 7, 1);
INSERT INTO `dp_admin_config` VALUES (7, 'web_site_copyright', '版权信息', 'base', 'text', '版权所有 © 福建省长城信息发展有限公司', '', '调用方式：<code>config(\'web_site_copyright\')</code>', '', '', '', '', '', 2, '', '', '', '', 1475241416, 1477710383, 8, 1);
